import React, { useState, useEffect }from 'react';
const AWS = require('aws-sdk');
const formidable = require('formidable');

// s3에 이미지파일, 텍스트(문자열)올리고 볼 수 있는 Create, Read 까지 구현해보자.
/*
    npm install aws-sdk --save

    그 후에 notion에 정리한 대로 개발환경을 구축했다.

    npm install formidable@latest --save
*/

function MakePictures() {

    const [tempAws, setTempAws] = useState(null);

    useEffect(()=>{
        setTempAws(new AWS.S3());
        console.log('S3 : ', tempAws);

    }, [])

    return (
        <div>
            {/* 아래의 태그들의 문법오류가 일어났다. */}
            {/* <form enctype="multipart/form-data" method="post" action="upload_receiver">
                <input type="file" name="userfile">
                    <input type="submit">
            </form> */}
            <form enctype="multipart/form-data" method="post" name="fileinfo">
                <label>Your email address:</label>
                <input type="email" autocomplete="on" autofocus name="userid" placeholder="email" required size="32" maxlength="64" /><br />
                <label>Custom file label:</label>
                <input type="text" name="filelabel" size="12" maxlength="32" /><br />
                <label>File to stash:</label>
                <input type="file" name="file" required />
                <input type="submit" value="Stash the file!" />
            </form>
        </div>
    )
}

export default MakePictures
